function initialItems() {
  const id = Math.floor(Math.random() * 1000) + 1 // Random ID between 1 and 1000
  const descriptions = ["Socks", "Shirt", "Hat", "Jeans", "Gloves"]
  const description =
    descriptions[Math.floor(Math.random() * descriptions.length)] // Random description from the array
  const quantity = Math.floor(Math.random() * 50) + 1 // Random quantity between 1 and 50
  const packed = Math.random() < 0.5 // Random boolean value for 'packed'

  return { id, description, quantity, packed }
}

export default function App() {
  return (
    <div>
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  )
}

function Logo() {
  return (
    <div>
      <h1>Travel Pickup üöó</h1>
    </div>
  )
}

function Form() {
  return (
    <div className="add-form">
      <h3>You need these for your trip.</h3>
    </div>
  )
}
function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} />
        ))}
      </ul>
    </div>
  )
}

function Item({ item }) {
  return (
    <li>
      <span>
        {item.description} {item.quantity}
      </span>
      <button>‚ùå</button>
    </li>
  )
}

function Stats() {
  return (
    <footer className="stats">
      you have X item in your bag and you need Y more.
    </footer>
  )
}
